// src/store.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.createStore({
  state: {
    isAuthenticated: false,
    userId: "",
    username: "",
    accessToken: null,
  },
  mutations: {
    setAuthentication(state, { isAuthenticated, userId, username, accessToken }) {
      state.isAuthenticated = isAuthenticated;
      state.userId = userId;
      state.username = username;
      state.accessToken = accessToken;
    },
  },
  actions: {
    authenticateUser({ commit }, { isAuthenticated, accessToken, username }) {
      commit('setAuthentication', { isAuthenticated, accessToken, username });
    },
  },
  getters: {
    isAuthenticated: (state) => state.isAuthenticated,
    userId: (state) => state.userId,
    username: (state) => state.username,
    accessToken: (state) => state.accessToken,
  },
});
